    const { address } = await result.waitForTransaction();
    console.log('\n✅ Token deployed at:', address);

    return address;
  } catch (err) {
    console.error('\nDeployment failed:', err);
    process.exit(1);
  }
}

deployDaimon().catch(console.error);
