const addresses = [
  '0x29d17C1A8D851d7d4cA97FAe97AcAdb398D9cCE0', // Clanker locker
  '0xE143f9872A33c955F23cF442BB4B1EFB3A7402A2', // MEV module
];

Promise.all(addresses.map(async addr => {
  const bal = await token.balanceOf(addr);
  return { addr, bal: ethers.formatUnits(bal, 18) };
})).then(results => {
  results.forEach(r => console.log(r.addr, ':', r.bal, 'DAIMON'));
});
""}
