{"command": "node -e "
const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider('https://mainnet.base.org');
const w = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, p);

async function check() {
  const feeLocker = '0xF3622742b1E446D92e45E22923Ef11C2fcD55D68';
  const weth = '0x4200000000000000000000000000000000000006';
  const daimon = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';

  // Check available fees - token param is the token BEING claimed
  const lockerAbi = ['function availableFees(address,address) view returns (uint256)'];
  const locker = new ethers.Contract(feeLocker, lockerAbi, p);

  const wethFees = await locker.availableFees(w.address, weth);
  console.log('Available WETH fees:', ethers.formatEther(wethFees));

  const daimonFees = await locker.availableFees(w.address, daimon);
  console.log('Available DAIMON fees:', ethers.formatEther(daimonFees));
}
check();
""}
