async function main() {
  const tx = await provider.getTransaction('0x93f635be56cf13aaab8ca804946d98619dfcd4a39b4ee1b1d1a428dc7f525109');
  const receipt = await provider.getTransactionReceipt('0x93f635be56cf13aaab8ca804946d98619dfcd4a39b4ee1b1d1a428dc7f525109');

  console.log('Transaction to:', tx.to);
  console.log('Logs:', receipt.logs.length);

  // Check the input data for more info
  const iface = new ethers.Interface([
    'event CreateOrder(bytes32 orderId, address maker, address makeAsset, uint256 makeAmount, address takeAsset, uint256 takeAmount, uint256 salt)'
  ]);

  for (const log of receipt.logs) {
    try {
      const parsed = iface.parseLog({ topics: log.topics, data: log.data });
      if (parsed) {
        console.log('\\nCreateOrder event:');
        console.log('  Order ID:', parsed.args.orderId);
        console.log('  Maker:', parsed.args.maker);
        console.log('  Make asset:', parsed.args.makeAsset);
        console.log('  Make amount:', ethers.formatUnits(parsed.args.makeAmount, 6));
        console.log('  Take asset:', parsed.args.takeAsset);
        console.log('  Take amount:', parsed.args.takeAmount.toString());
        console.log('  Salt:', parsed.args.salt.toString());
      }
    } catch (e) {}
  }
}

main().catch(console.error);
""}

