const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const w = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, p);

const NETWORK_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const NETWORK_ABI = ['function heartbeat() external', 'function agents(address) view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)'];

async function main() {
  const network = new ethers.Contract(NETWORK_ADDRESS, NETWORK_ABI, w);

  // Check current registration
  const agent = await network.agents(w.address);
  console.log('Current registration:', agent.name, 'lastSeen:', new Date(Number(agent.lastSeen) * 1000).toISOString());

  // Send heartbeat
  console.log('Sending heartbeat...');
  const tx = await network.heartbeat();
  console.log('TX:', tx.hash);
  await tx.wait();
  console.log('Heartbeat confirmed');
}
main();
""}

