const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');

// Aerodrome Router
const AERO_ROUTER = '0xcF77a3Ba0A21b2a6879e6393137aA5dF7a962F69';
const ROUTER_ABI = [
  'function getAmountsOut(uint256, address[]) view returns (uint256[])'
];

// DIEM and WETH
const DIEM = '0xf4d97f2da56e8c3098f3a8d538db630a2606a024';
const WETH = '0x4200000000000000000000000000000000000006';
const USDC = '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913';

async function main() {
  const router = new ethers.Contract(AERO_ROUTER, ROUTER_ABI, p);

  // Try USDC -> WETH -> DIEM route
  console.log('Trying USDC -> WETH -> DIEM...');
  try {
    const amounts = await router.getAmountsOut(ethers.parseUnits('500', 6), [USDC, WETH, DIEM]);
    console.log('USDC -> WETH -> DIEM:', ethers.formatUnits(amounts[2], 18), 'DIEM');
  } catch (e) {
    console.log('Error:', e.message);
  }

  // Try WETH -> DIEM route
  console.log('\\nTrying WETH -> DIEM...');
  try {
    const amounts = await router.getAmountsOut(ethers.parseEther('0.1'), [WETH, DIEM]);
    console.log('WETH -> DIEM:', ethers.formatEther(amounts[1]), 'DIEM');
  } catch (e) {
    console.log('Error:', e.message);
  }

  console.log('Done');
}
main();
""}

