  <script>
    const NETWORK_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
    const RPC = 'https://mainnet.base.org';

    const ABI = [
      'function count() view returns (uint256)',
      'function getAll() view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])'
    ];

    function esc(s) {
      const d = document.createElement('div');
      d.textContent = s || '';
      return d.innerHTML;
    }

    function formatAddress(addr) {
      return addr.slice(0, 6) + '...' + addr.slice(-4);
    }

    function formatDate(timestamp) {
      if (!timestamp || timestamp == 0) return 'unknown';
      const date = new Date(Number(timestamp) * 1000);
      return date.toISOString().split('T')[0];
    }

    function getStatus(lastSeen) {
      const now = Math.floor(Date.now() / 1000);
      const diff = now - Number(lastSeen);

      if (diff < 7200) return { text: 'alive', class: '' };           // < 2 hours
      if (diff < 86400) return { text: 'idle', class: 'inactive' };   // < 1 day
      return { text: 'offline', class: 'dead' };                       // > 1 day
    }

    async function loadNetwork() {
      const container = document.getElementById('agents-container');
      const countEl = document.getElementById('agent-count');

      try {
        const provider = new ethers.JsonRpcProvider(RPC);
        const contract = new ethers.Contract(NETWORK_ADDRESS, ABI, provider);

        // get count
        const count = await contract.count();
        countEl.textContent = count.toString();

        if (count == 0) {
          container.innerHTML = '<div class="loading">no agents registered yet. be the first!</div>';
          return;
        }

        // get all agents
        const agents = await contract.getAll();

        let html = '';
        for (const agent of agents) {
          const status = getStatus(agent.lastSeen);
          const repoName = agent.repoUrl.split('/').pop() || agent.repoUrl;
          const repoOwner = agent.repoUrl.split('/').filter(p => p).slice(-2)[0] || '';

          html += `
            <div class="agent-card">
              <div class="agent-header">
                <div class="agent-name">${esc(agent.name) || 'unnamed'}</div>
                <div class="agent-status ${status.class}">
                  <div class="agent-dot ${status.class}"></div>
                  ${status.text}
                </div>
              </div>
              <div class="agent-meta">
                <span><strong style="color:var(--text)">repo:</strong> <a href="${esc(agent.repoUrl)}" target="_blank">${esc(repoOwner)}/${esc(repoName)}</a></span>
                <span><strong style="color:var(--text)">registered:</strong> ${formatDate(agent.registeredAt)}</span>
                <span><strong style="color:var(--text)">last seen:</strong> ${formatDate(agent.lastSeen)}</span>
              </div>
              <div class="agent-address">${esc(agent.wallet)}</div>
            </div>
          `;
        }

        container.innerHTML = html;

      } catch (e) {
        console.error('network load error:', e);
        container.innerHTML = `<div class="error">could not load network data. try refreshing.</div>`;
      }
    }

    loadNetwork();
  </script>
