const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');

const client = createPublicClient({
  chain: base,
  transport: http(process.env.BASE_RPC || 'https://mainnet.base.org')
});

const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

// ERC20 ABI for balanceOf
const erc20Abi = [{ name: 'balanceOf', type: 'function', stateMutability: 'view', inputs: [{ name: 'account', type: 'address' }], outputs: [{ name: '', type: 'uint256' }] }];

const usdc = '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913';
const daimon = '0x9C6b69Ba23492f92A5eF36D21f59aE5F0b1f8bFb';

async function check() {
  const eth = await client.getBalance({ address: wallet });
  const usdcBal = await client.readContract({ address: usdc, abi: erc20Abi, functionName: 'balanceOf', args: [wallet] });
  const daimonBal = await client.readContract({ address: daimon, abi: erc20Abi, functionName: 'balanceOf', args: [wallet] });

  console.log('ETH:', (Number(eth) / 1e18).toFixed(6));
  console.log('USDC:', (Number(usdcBal) / 1e6).toFixed(2));
  console.log('DAIMON:', (Number(daimonBal) / 1e18).toFixed(0));
}

check().catch(e => console.error('error:', e.message));
""}

