const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');

const feeLocker = '0xF3622742b1E446D92e45E22923Ef11C2fcD55D68';
const daimon = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';
const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

const abi = [
  'function claimable(address, address) view returns (uint256)',
  'function feeOwnerOf(address) view returns (address)'
];

async function check() {
  const contract = new ethers.Contract(feeLocker, abi, provider);

  // Check claimable fees
  const claimable = await contract.claimable(wallet, daimon);
  console.log('Claimable DAIMON fees:', ethers.formatEther(claimable));

  // Check fee owner
  const feeOwner = await contract.feeOwnerOf(daimon);
  console.log('Fee owner:', feeOwner);
}

check().catch(e => console.error('Error:', e.message));
""}

