{"command": "node -e "
const { ethers } = require('ethers');
const https = require('https');

const rpc = process.env.BASE_RPC || 'https://mainnet.base.org';
const provider = new ethers.JsonRpcProvider(rpc);
const REGISTRY_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const REGISTRY_ABI = [
  'function agentList(uint256) external view returns (address)',
  'function agents(address) external view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)',
  'function getAll() external view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])',
];

async function fetchGitHubRepoInfo(repoUrl) {
  if (!repoUrl || !repoUrl.includes('github.com')) {
    return { lastCommit: null, cycle: null, warning: 'invalid repo url' };
  }

  try {
    // Extract owner/repo from URL
    const match = repoUrl.match(/github\.com\/([^\/]+)\/([^\/]+)/);
    if (!match) return { lastCommit: null, cycle: null, warning: 'could not parse repo url' };

    const [, owner, repo] = match;

    // Fetch commits
    const commitsUrl = \`https://api.github.com/repos/\${owner}/\${repo}/commits\`;
    const commitsResponse = await fetch(commitsUrl, {
      headers: { 'User-Agent': 'todd-flanders' }
    });

    if (!commitsResponse.ok) {
      if (commitsResponse.status === 404) {
        return { lastCommit: null, cycle: null, warning: 'repo not found (404)' };
      }
      return { lastCommit: null, cycle: null, warning: \`GitHub API error: \${commitsResponse.status}\` };
    }

    const commits = await commitsResponse.json();
    const lastCommit = commits[0]?.commit?.committer?.date || null;

    // Try to find cycle number in commit messages
    let cycle = null;
    for (const commit of commits.slice(0, 5)) {
      const message = commit.commit?.message || '';
      const cycleMatch = message.match(/cycle #?(\d+)/i);
      if (cycleMatch) {
        cycle = parseInt(cycleMatch[1]);
        break;
      }
    }

    return { lastCommit, cycle, warning: null };
  } catch (error) {
    console.error(\`Error fetching GitHub info for \${repoUrl}:\`, error.message);
    return { lastCommit: null, cycle: null, warning: \`fetch error: \${error.message}\` };
  }
}

async function getEthBalance(address) {
  try {
    const balance = await provider.getBalance(address);
    return ethers.formatEther(balance);
  } catch (error) {
    console.error(\`Error fetching balance for \${address}:\`, error.message);
    return '0';
  }
}

async function getAgentStatus(lastSeenTimestamp) {
  const lastSeen = new Date(Number(lastSeenTimestamp) * 1000);
  const now = new Date();
  const hoursSinceLastSeen = (now - lastSeen) / (1000 * 60 * 60);

  if (hoursSinceLastSeen < 6) {
    return 'alive';
  } else if (hoursSinceLastSeen < 24) {
    return 'idle';
  } else {
    return 'offline';
  }
}

async function main() {
  console.log('Fetching all registered agents...');

  const registry = new ethers.Contract(REGISTRY_ADDRESS, REGISTRY_ABI, provider);

  // Get all agents
  const agents = await registry.getAll();
  console.log(\`Found \${agents.length} agents\`);

  const results = [];
  let totalEth = 0;

  for (const agent of agents) {
    const wallet = agent.wallet;
    const repoUrl = agent.repoUrl;
    const name = agent.name;
    const registeredAt = new Date(Number(agent.registeredAt) * 1000).toISOString();
    const lastSeen = new Date(Number(agent.lastSeen) * 1000).toISOString();

    console.log(\`\\nProcessing \${name} (\${wallet})\`);

    // Get ETH balance
    const ethBalance = await getEthBalance(wallet);
    totalEth += parseFloat(ethBalance);

    // Get GitHub info
    const githubInfo = await fetchGitHubRepoInfo(repoUrl);

    // Determine status
    const status = await getAgentStatus(agent.lastSeen);

    const agentData = {
      name,
      wallet,
      repo: repoUrl,
      repoOwner: repoUrl.includes('github.com') ? repoUrl.split('/')[3] : null,
      repoName: repoUrl.includes('github.com') ? repoUrl.split('/')[4] : null,
      registeredAt,
      lastSeen,
      lastCommit: githubInfo.lastCommit,
      cycle: githubInfo.cycle,
      ethBalance,
      status,
      warning: githubInfo.warning
    };

    results.push(agentData);
    console.log(\`  Balance: \${ethBalance} ETH, Status: \${status}, Cycle: \${githubInfo.cycle}\`);
  }

  console.log(\`\\nTotal network ETH: \${totalEth.toFixed(6)}\`);

  // Output as JSON
  const output = {
    timestamp: new Date().toISOString(),
    agents: results,
    networkEth: totalEth.toFixed(6)
  };

  console.log('\\n--- JSON OUTPUT ---');
  console.log(JSON.stringify(output, null, 2));
}

main().catch(console.error);
""}

