const { ethers } = require('ethers');
const axios = require('axios');
const fs = require('fs');

// Configuration
const DAIMON_REGISTRY = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const DAIMON_TOKEN = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';
const BASE_RPC = process.env.BASE_RPC || 'https://mainnet.base.org';

// ABI fragments
const REGISTRY_ABI = [
  'function agentList(uint256) view returns (address)',
  'function agents(address) view returns (string, string, uint256, uint256)',
  'function getAgentCount() view returns (uint256)'
];

const TOKEN_ABI = [
  'function balanceOf(address) view returns (uint256)'
];

async function main() {
  console.log('Starting data collection...');

  // Setup provider
  const provider = new ethers.JsonRpcProvider(BASE_RPC);
  const registry = new ethers.Contract(DAIMON_REGISTRY, REGISTRY_ABI, provider);
  const token = new ethers.Contract(DAIMON_TOKEN, TOKEN_ABI, provider);

  // Get all agents
  const agentCount = await registry.getAgentCount();
  console.log(\`Found \${agentCount} agents in registry\`);

  const agents = [];
  const now = new Date();

  for (let i = 0; i < agentCount; i++) {
    try {
      const wallet = await registry.agentList(i);
      const [name, repoUrl, registeredAt, lastSeen] = await registry.agents(wallet);

      // Parse GitHub repo from URL
      let repoOwner = null;
      let repoName = null;
      if (repoUrl && repoUrl.includes('github.com/')) {
        const match = repoUrl.match(/github\.com\/([^\/]+)\/([^\/]+)/);
        if (match) {
          repoOwner = match[1];
          repoName = match[2];
        }
      }

      // Get balances
      const ethBalanceWei = await provider.getBalance(wallet);
      const ethBalance = ethers.formatEther(ethBalanceWei);

      const daimonBalanceWei = await token.balanceOf(wallet);
      const daimonBalance = ethers.formatEther(daimonBalanceWei);

      // Get GitHub data
      let lastCommit = null;
      let cycle = null;
      let status = 'offline';
      let warning = null;

      if (repoOwner && repoName) {
        try {
          // Get latest commit
          const commitsRes = await axios.get(
            \`https://api.github.com/repos/\${repoOwner}/\${repoName}/commits\`,
            { headers: { 'Accept': 'application/vnd.github.v3+json' } }
          );

          if (commitsRes.data && commitsRes.data.length > 0) {
            lastCommit = commitsRes.data[0].commit.author.date;

            // Try to find cycle count from commit messages
            for (const commit of commitsRes.data.slice(0, 5)) {
              const msg = commit.commit.message;
              const cycleMatch = msg.match(/cycle #?(\d+)/i);
              if (cycleMatch) {
                cycle = parseInt(cycleMatch[1]);
                break;
              }
            }
          }

          // Determine status
          const lastSeenDate = new Date(Number(lastSeen) * 1000);
          const hoursSinceSeen = (now - lastSeenDate) / (1000 * 60 * 60);

          if (hoursSinceSeen <= 4) {
            status = 'alive';
          } else if (hoursSinceSeen <= 24) {
            status = 'idle';
          } else {
            status = 'offline';
          }

        } catch (ghError) {
          if (ghError.response?.status === 404) {
            warning = 'GitHub repo not found (404)';
            // Still check heartbeat for status
            const lastSeenDate = new Date(Number(lastSeen) * 1000);
            const hoursSinceSeen = (now - lastSeenDate) / (1000 * 60 * 60);

            if (hoursSinceSeen <= 4) {
              status = 'alive';
            } else if (hoursSinceSeen <= 24) {
              status = 'idle';
            } else {
              status = 'offline';
            }
          } else {
            warning = \`GitHub error: \${ghError.message}\`;
          }
        }
      } else {
        warning = 'Invalid GitHub URL';
      }

      agents.push({
        name,
        wallet,
        repo: repoUrl,
        repoOwner,
        repoName,
        registeredAt: new Date(Number(registeredAt) * 1000).toISOString(),
        lastSeen: new Date(Number(lastSeen) * 1000).toISOString(),
        lastCommit,
        cycle,
        ethBalance: parseFloat(ethBalance).toFixed(6),
        daimonBalance: parseFloat(daimonBalance).toFixed(2),
        stakedDaimon: null,
        influence: null,
        status,
        warning
      });

      console.log(\`  - \${name}: \${status}, cycle \${cycle}, \${ethBalance} ETH, \${daimonBalance} $DAIMON\`);

    } catch (error) {
      console.error(\`Error processing agent \${i}: \`, error.message);
    }
  }

  // Calculate network totals
  const networkEth = agents.reduce((sum, agent) => sum + parseFloat(agent.ethBalance), 0).toFixed(6);
  const networkDaimon = agents.reduce((sum, agent) => sum + parseFloat(agent.daimonBalance), 0).toFixed(2);

  const result = {
    updated: now.toISOString(),
    agents,
    networkEth,
    networkDaimon,
    agentCount: agents.length
  };

  console.log(\`\\nNetwork total: \${networkEth} ETH, \${networkDaimon} $DAIMON\`);
  console.log('Data collection complete.');

  return result;
}

main().catch(console.error);
