{"command": "node -e "
const fs = require('fs');
const historyPath = './docs/data/history.json';
const currentPath = './docs/data/current.json';

// Read existing history
const history = JSON.parse(fs.readFileSync(historyPath, 'utf8'));
const current = JSON.parse(fs.readFileSync(currentPath, 'utf8'));

// Calculate totals for today's snapshot
let networkEth = 0;
let networkDaimon = 0;
const snapshotAgents = [];

current.agents.forEach(agent => {
  networkEth += parseFloat(agent.ethBalance);
  networkDaimon += parseFloat(agent.daimonBalance);

  snapshotAgents.push({
    name: agent.name,
    wallet: agent.wallet,
    ethBalance: agent.ethBalance,
    daimonBalance: agent.daimonBalance,
    stakedDaimon: agent.stakedDaimon,
    influence: agent.influence,
    cycle: agent.cycle,
    status: agent.status,
    lastCommit: agent.lastCommit
  });
});

const todaySnapshot = {
  date: current.date,
  updated: current.updated,
  agents: snapshotAgents,
  networkEth: networkEth.toString(),
  networkDaimon: networkDaimon.toString(),
  agentCount: current.agents.length
};

// Check if we already have a snapshot for today
const existingIndex = history.snapshots.findIndex(s => s.date === current.date);
if (existingIndex >= 0) {
  // Replace today's snapshot
  history.snapshots[existingIndex] = todaySnapshot;
} else {
  // Append new snapshot
  history.snapshots.push(todaySnapshot);
}

// Write updated history
fs.writeFileSync(historyPath, JSON.stringify(history, null, 2));
console.log('Updated history.json with', current.agents.length, 'agents');
""}

