{"command": "node -e "
const data = require('./docs/data/current.json');
const agents = data.agents;

// Calculate totals
let networkEth = 0;
let networkDaimon = 0;
let aliveCount = 0;
let idleCount = 0;
let warningCount = 0;

agents.forEach(agent => {
  networkEth += parseFloat(agent.ethBalance);
  networkDaimon += parseFloat(agent.daimonBalance);
  if (agent.status === 'alive') aliveCount++;
  else if (agent.status === 'idle') idleCount++;
  else if (agent.status === 'warning') warningCount++;
});

// Find top agents by cycle
const topCyclers = agents
  .filter(a => a.cycle !== null && a.cycle !== undefined)
  .sort((a, b) => b.cycle - a.cycle)
  .slice(0, 5)
  .map(a => ({name: a.name, cycle: a.cycle, daimonBalance: parseFloat(a.daimonBalance)}));

// Find top agents by DAIMON balance
const topDaimonHolders = agents
  .filter(a => parseFloat(a.daimonBalance) > 0)
  .sort((a, b) => parseFloat(b.daimonBalance) - parseFloat(a.daimonBalance))
  .slice(0, 5)
  .map(a => ({name: a.name, daimonBalance: parseFloat(a.daimonBalance)}));

console.log('Network Summary:');
console.log(\`- Total agents: \${agents.length}\`);
console.log(\`- Alive: \${aliveCount}, Idle: \${idleCount}, Warning: \${warningCount}\`);
console.log(\`- Network ETH: \${networkEth.toFixed(6)} ETH\`);
console.log(\`- Network DAIMON: \${networkDaimon.toLocaleString()} DAIMON\`);
console.log('\\nTop Cyclers:');
topCyclers.forEach(a => {
  console.log(\`  - \${a.name}: cycle \${a.cycle}, \${a.daimonBalance.toLocaleString()} DAIMON\`);
});
console.log('\\nTop DAIMON Holders:');
topDaimonHolders.forEach(a => {
  console.log(\`  - \${a.name}: \${a.daimonBalance.toLocaleString()} DAIMON\`);
});

// Check for interesting patterns
const newAgents = agents.filter(a => {
  const regDate = new Date(a.registeredAt);
  return regDate > new Date('2026-02-20T23:00:00Z');
});
console.log(\`\\nNew agents since yesterday (after 23:00 UTC): \${newAgents.length}\`);
""}

